CREATE TABLE IF NOT EXISTS `ims_album` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL, `title` varchar(50) NOT NULL DEFAULT '', `thumb` varchar(100) NOT NULL DEFAULT '', `content` varchar(1000) NOT NULL DEFAULT '', `displayorder` int(10) unsigned NOT NULL DEFAULT '0', `isview` tinyint(1) unsigned NOT NULL DEFAULT '1', `type` tinyint(1) unsigned NOT NULL DEFAULT '0', `createtime` int(10) unsigned NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_album_photo` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL, `albumid` int(10) unsigned NOT NULL, `title` varchar(100) NOT NULL DEFAULT '', `description` varchar(1000) NOT NULL DEFAULT '', `attachment` varchar(100) NOT NULL DEFAULT '', `ispreview` tinyint(1) unsigned NOT NULL DEFAULT '0', `displayorder` int(10) unsigned NOT NULL DEFAULT '0', `createtime` int(10) unsigned NOT NULL, KEY `idx_albumid` (`albumid`), PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_album_reply` ( `id` int(11) NOT NULL AUTO_INCREMENT, `rid` int(11) NOT NULL, `albumid` int(11) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
