manifest.install:
CREATE TABLE IF NOT EXISTS `ims_forward_data` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL COMMENT '公众号ID', `from_user` varchar(50) not null default '' COMMENT '用户openid', `rid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '规则id', `uid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '分享人UID', `shareip` varchar(15) NOT NULL DEFAULT '' COMMENT '转发有礼IP', `sharetime` int unsigned NOT NULL COMMENT '分享时间', `viewnum` int(10) unsigned NOT NULL DEFAULT '1' COMMENT '查看次数', PRIMARY KEY ( `id` ) ) ENGINE = MYISAM DEFAULT CHARSET =utf8;
CREATE TABLE IF NOT EXISTS`ims_forward_list` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '规则id', `weid` int(10) unsigned NOT NULL COMMENT '公众号ID', `from_user` varchar(50) not null default '' COMMENT '用户openid', `sharenum` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '分享量', `sharetime` int unsigned NOT NULL COMMENT '最后分享时间', `status` tinyint unsigned NOT NULL default 1 comment '是否禁止', PRIMARY KEY ( `id` ) ) ENGINE = MYISAM DEFAULT CHARSET =utf8;
CREATE TABLE IF NOT EXISTS `ims_forward_reply` ( `id` int( 10 ) unsigned NOT NULL AUTO_INCREMENT , `rid` int( 10 ) unsigned NOT NULL default 0 comment '规则id', `parentid` int(10) unsigned NOT NULL DEFAULT '0', `title` varchar( 50 ) NOT NULL comment '规则标题', `credit` int( 10 ) unsigned NOT NULL default '0' comment '奖励积分', `shareurl` varchar( 255 ) NOT NULL comment '活动网址', `picture` varchar( 100 ) NOT NULL comment '图片', `start_time` int unsigned NOT NULL comment '开始时间', `end_time` int unsigned NOT NULL comment '结束时间', `description` varchar( 255 ) NOT NULL comment '描述', `content` text NOT NULL comment '内容', `status` tinyint unsigned NOT NULL default 1 comment '开关状态', PRIMARY KEY ( `id` ) ) ENGINE = MYISAM DEFAULT CHARSET =utf8;
