manifest.install:
CREATE TABLE IF NOT EXISTS `ims_scratchcard_award` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL COMMENT '规则ID', `title` varchar(50) NOT NULL COMMENT '奖品名称', `total` int(11) NOT NULL COMMENT '数量', `probalilty` varchar(5) NOT NULL COMMENT '概率单位%', `description` varchar(100) NOT NULL DEFAULT '' COMMENT '描述', `inkind` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否是实物', `activation_code` text COMMENT '激活码', `activation_url` varchar(200) NOT NULL COMMENT '激活地址', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_scratchcard_reply` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL COMMENT '规则ID', `picture` varchar(100) NOT NULL COMMENT '活动图片', `description` varchar(100) NOT NULL COMMENT '活动描述', `rule` varchar(1000) NOT NULL COMMENT '规则', `periodlottery` SMALLINT( 10 ) UNSIGNED NOT NULL DEFAULT '1' COMMENT '0为无周期', `maxlottery` tinyint(3) unsigned NOT NULL COMMENT '最大抽奖数', `hitcredit` int(11) NOT NULL COMMENT '中奖奖励积分', `misscredit` int(11) NOT NULL COMMENT '未中奖奖励积分', `background` varchar(255) NOT NULL DEFAULT '', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_scratchcard_winner` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL COMMENT '规则ID', `aid` int(10) unsigned NOT NULL COMMENT '奖品ID', `award` varchar(100) NOT NULL DEFAULT '' COMMENT '奖品名称', `description` varchar(500) NOT NULL DEFAULT '' COMMENT '中奖信息描述', `from_user` varchar(50) NOT NULL COMMENT '用户唯一身份ID', `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0未领奖，1不需要领奖，2已领奖', `createtime` int(10) unsigned NOT NULL COMMENT '获奖日期', PRIMARY KEY (`id`), KEY `idx_createtime_fromuser` (`createtime`,`from_user`), KEY `idx_fromuser_rid` (`from_user`,`rid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 ;
