CREATE TABLE `ims_share_data` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL COMMENT '规则id', `from_user` varchar(50) not null default '' COMMENT '用户openid', `shareip` varchar(15) NOT NULL DEFAULT '' COMMENT '分享达人IP', `sharetime` int unsigned NOT NULL COMMENT '分享时间', PRIMARY KEY ( `id` ) ) ENGINE = MYISAM DEFAULT CHARSET = utf8;
CREATE TABLE `ims_share_list` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL COMMENT '规则id', `from_user` varchar(50) not null default '' COMMENT '用户openid', `name` varchar(50) not null default '' COMMENT '姓名', `tel` varchar(50) not null default '' COMMENT '电话', `sharenum` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '分享量', `sharetime` int unsigned NOT NULL COMMENT '最后分享时间', `status` tinyint unsigned NOT NULL default 1 comment '是否禁止', PRIMARY KEY ( `id` ) ) ENGINE = MYISAM DEFAULT CHARSET = utf8;
CREATE TABLE `ims_share_reply` ( `id` int( 10 ) unsigned NOT NULL AUTO_INCREMENT , `rid` int( 10 ) unsigned NOT NULL default 0 comment '规则id', `parentid` int(10) unsigned NOT NULL DEFAULT '0', `title` varchar( 50 ) NOT NULL comment '规则标题', `checkkeyword` varchar( 50 ) NOT NULL comment '查询关键词', `picture` varchar( 100 ) NOT NULL comment '图片', `start_time` int unsigned NOT NULL comment '开始时间', `end_time` int unsigned NOT NULL comment '结束时间', `description` varchar( 255 ) NOT NULL comment '描述', `content` text NOT NULL comment '内容', `status` tinyint unsigned NOT NULL default 1 comment '开关状态', PRIMARY KEY ( `id` ) ) ENGINE = MYISAM DEFAULT CHARSET = utf8;

