CREATE TABLE `ims_lxy_rtrouter_authentication` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) DEFAULT NULL, `routerid` int(11) DEFAULT NULL, `fromuser` varchar(100) DEFAULT NULL, `createtime` int(11) DEFAULT NULL, `result` int(11) DEFAULT NULL, `resultmemo` varchar(255) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE `ims_lxy_rtrouter_info` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) DEFAULT NULL, `iurl` varchar(255) DEFAULT NULL, `rname` varchar(100) DEFAULT NULL, `appid` varchar(100) DEFAULT NULL, `appkey` varchar(100) DEFAULT NULL, `nodeid` varchar(100) DEFAULT NULL, `status` tinyint(4) DEFAULT '1', PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
CREATE TABLE `ims_lxy_rtrouter_reply` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '规则id', `oktip` varchar(255) NOT NULL COMMENT '规则标题', `routerid` int(10) unsigned NOT NULL COMMENT '名片id', `status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '开关状态', PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;
